import React from 'react'

const Part = (props) => {
  return (
    <div>
      <p>This part is {props.part}, Exercisesnumber is {props.exercises} </p>
    </div>

  )
}


const Header = (props) => {
  return (
    <div>
    <p>This course is {props.course}</p>
    </div>
  )
}

const Content = () => {
  const part = ['Fundamental of React', 'Using props to pass data', 'State of a component']
  const exercises = ['10', '7', '14']
  return (
    <div>
      <Part part = {part[0]} exercises = {exercises[0]} />
      <Part part = {part[1]} exercises = {exercises[1]} />
      <Part part = {part[2]} exercises = {exercises[2]} />
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
    <p>Total exercises {props.enumber}</p>
    </div>
  )
}


const App = () => {

  return (
    <div>
      <Header course = {course} />
      <Content />
    </div>
  )
}

export default App;
