//import logo from './logo.svg';
//import './App.css';
import React from 'react'

const Part = () => {
  const part = ['Fundamental of React', 'Using props to pass data', 'State of a component']
  const exercises = ['10', '7', '14']
  return (
    <div>
      <p>This part is {part}, Exercisesnumber is {exercises} </p>
    </div>

  )
}


const Header = (props) => {
  return (
    <div>
    <p>This course is {props.course}</p>
    </div>
  )
}

const Content = () => {
  return (
    <div>
      <Part partnum = {part[0]} exenum = {exercises[0]} />
      <Part partnum = {part[1]} exenum = {exercises[1]} />
      <Part partnum = {part[2]} exenum = {exercises[2]} />
     //<p>Partname is {props.partname}, exercisesnumber is {props.exenum}</p>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
    <p>Total exercises {props.enumber}</p>
    </div>
  )
}


const App = () => {

  const course = 'Half Stack application development'
  const part1  = 'Fundamental of React'
  const exercises1 = 10
  const part2  = 'Using props to pass data'
  const exercises2 = 7
  const part3  = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header course = {course} />
      <Content />
    //  <Content partname = {part1} exenum = {exercises1} />
    //  <Content partname = {part2} exenum = {exercises2} />
    //  <Content partname = {part3} exenum = {exercises3} />
    //  <Total enumber = {exercises1 + exercises2 + exercises3} />
    </div>
  )
}

export default App;
